handlebarsPartials.handle_404:
{{#if data.primaryResponse.hits.hits.[0] ~}}
    {{> @partial-block }}
{{else}}
    <div class="mdc-layout-grid">
        <div class="mdc-layout-grid__inner">
            <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-8-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone content page-content">
                <h2>The page you requested could not be found</h2>
                <p>
                    We recently relaunched our website and some page addresses may have changed.
                </p>
                {{#ifAll data.supplimentaryResponses.not_found (compare data.supplimentaryResponses.not_found.hits.total ">" 0) ~}}
                <h3>Did you mean?</h3>
                {{#each data.supplimentaryResponses.not_found.hits.hits ~}}
                    {{#compare @index "===" 0 ~}}
                    {{>highlight_result}}
                    {{else}}
                    {{/compare ~}}
                {{/each ~}}
                {{/ifAll ~}}
            </div>
            <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone">
                <img src="/assets/images/404.png" class="responsive">
            </div>
        </div>
    </div>
    {{#ifAll data.supplimentaryResponses.not_found (compare data.supplimentaryResponses.not_found.hits.total ">" 1) ~}}
        <hr />
        <h3>Other relevant content</h3>
        {{#each data.supplimentaryResponses.not_found.hits.hits ~}}
            {{#compare @index "===" 0 ~}}
            {{else}}
            {{>highlight_result}}
            {{/compare ~}}
        {{/each ~}}
    {{else}}
        <h3>Try searching for a topic</h3>
    {{/ifAll ~}}

    <script>
        gtag('event', '404', {
            event_category: '{{name}}',
            event_label: '{{params.uri.pathname}}'
        });
    </script>
{{/if ~}}

handlebarsPartials.related_posts:
{{#compare data.supplimentaryResponses.related_posts.hits.total ">" 0 ~}}
<div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-12 related page-related">
    <h3 class="blogs">Related posts</h3>
    <div class="mdc-layout-grid">
        <div class="mdc-layout-grid__inner">
            {{#each data.supplimentaryResponses.related_posts.hits.hits ~}}
            <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone">
                {{{_source.rendered.scvo_gist}}}
            </div>
            {{/each ~}}
        </div>
    </div>
</div>
{{/compare ~}}

handlebarsPartials.breadcrumbs:
{{#with data.primaryResponse.hits.hits.[0]._source ~}}
<ul class="breadcrumbs breadcrumbs-length-{{breadcrumb.length}}">
    <li><a href="/">Home</a></li>
    {{#each breadcrumb ~}}
    <li><a href="{{replace url 'https://cms.scvo.org' ''}}">{{{striptags title}}}</a></li>
    {{/each ~}}
    <li>{{{striptags title}}}</li>
</ul>
{{/with ~}}

default.head:
{{#if data.primaryResponse.hits.hits.[0] ~}}
{{#with data.primaryResponse.hits.hits.[0]._source ~}}
{{#ifAny title og_title ~}}
<title>SCVO â€“ {{{default title og_title}}}</title>
<meta property="og:title" content="SCVO â€“ {{{default title og_title}}}" data-dynamic="true">
